import React from 'react';
import {PuffLoader} from "react-spinners";

const style = {
	width: "100%",  
	height: "100",
    display: "flex",
    justifyContent: "center",
    alignItems: "center"
}

function Loader(props) {
	document.body.style = 'background: white';

	return (
      <div style={{width: "100vw",  height: "40vw", border : '1px solid', display: "flex", justifyContent: "center", alignItems: "center"}}>
	      <PuffLoader
	       loading={true}
	       color={"#B0DFE5"}
	      />
      </div>
	);
}

export default Loader
