import React from 'react'
import { makeStyles } from '@material-ui/core/styles';
import Hidden from '@material-ui/core/Hidden';
import Shipping from './static/shipping.png';
import { motion } from "framer-motion";

const useStyles = makeStyles((theme) => ({
	 mainbox : {
	  margin : 'auto',
	  width : '100%',
	  height : '600px',
	},
	mobilebox : {
	  margin : 'auto',
	  width : '100%',
	  height : '560px',
	  padding : '240px 70px'

	},


})
);

const variants = {
  active: {
    x: "-150px",
  },
  inActive: {
    x: "1350px",
  }
};

function Checkout(){
	const classes = useStyles();
	const [movement, setMovement] = React.useState(false);
	document.body.style = 'background: #B0DFE5';

	React.useEffect(() => {
	    setInterval(setMovement(true), 1000);
	  });

	const changecolorin = (e) => {
		e.target.style.color = '#B0DFE5';
	}

	const changecolorout = (e) => {
		e.target.style.color = 'white';
	}

	const home = () => {
		window.location.href = 'http://localhost:3000';
	}
	return(
		<>
		 <Hidden xsDown>
			<div className={classes.mainbox} >
		    	<motion.img 
		    	animate={movement ? "active" : "inActive"}
     		    variants={variants}
		    	src={Shipping} 
		    	alt='Shipped' 
		    	style={{position : 'absolute', top : '40%', left : '40%', paddingLeft : '40px'}}
		    	/>
		        <br/>
		        <br/>

		    	<p style={{fontSize : '34px', color : 'white', position : 'absolute', top : '45%', left : '26%', paddingRight : '150px', lineHeight : '1.4'}}>Order #3321 has been shipped <span style={{textDecoration : 'underline', cursor : 'pointer'}} onMouseOver={changecolorin} onMouseLeave={changecolorout} onClick={home}>LaptopNg</span></p>
		    </div>
		  </Hidden>

		  <Hidden smUp>

		  	<div className={classes.mobilebox}>
		    	<img src={Shipping} alt='Shipped' style={{paddingLeft : '50px'}}/>
		    	<br/>
		    	<br/>
		        <br/>

		    	<p style={{fontSize : '25px', color : 'white', lineHeight : '0.9'}}>Order #3321 has been shipped <span style={{textDecoration : 'underline', cursor : 'pointer'}} onClick={home}>LaptopNg</span></p>
		    </div>
		  </Hidden>
		 </>
		);
}

export default Checkout
