import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Hidden from '@material-ui/core/Hidden';
import {Link} from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import IconButton from '@material-ui/core/IconButton';
import Button from '@material-ui/core/Button';
import MenuIcon from '@material-ui/icons/Menu';
import Drawers from './drawer';
import CartPop from './cartpop';
import Popover from '@material-ui/core/Popover'; //handle cart popover

//styles

const useStyles = makeStyles((theme) => ({
	title: {
		paddingLeft : '20px',
		flexGrow : 1,
	},

	title2:{
		marginRight : theme.spacing(1),
		fontSize : 'xl',
	},

	Mobileicon: {
		paddingLeft :  '30%',
		flexGrow : 1,
	},



}));


//component
function NavBar(){
	const classes = useStyles();
	const [mobilebar, setMobilebar] = React.useState(false);
	const [anchorEl, setAnchorEl] = React.useState(null);


	//const theme = useTheme();

	function handleDrawerToggle() {
    	setMobilebar(!mobilebar); //toggles the mobilebar state
  	}

  	function handleClick(e){
  		setAnchorEl(e.currentTarget); //toggles cart 
  	}

  	function handleClose(e){
  		setAnchorEl(null);
  	}

  	const open = Boolean(anchorEl);
  	const id = open ? 'simple-popover' : undefined;

		return (
			<div>
				<AppBar position = 'static' style={{background : '#B0DFE5'}}>
					<Toolbar>
						<Hidden xsDown>

							<Link to='/' className="brand-logo"><img src="./laptop_ng.png" /></Link>

							<Typography variant="h6" color='inherit' className={classes.title}>
								<Link to='/' className="brand-logo" style={{color : 'inherit'}}>LaptopNG</Link>
							</Typography>

							<Button color='inherit' className={classes.title2}>
								<Link to='/' className="brand-logo" style={{color : 'inherit'}}>Signup</Link>	
							</Button>
							<Button color = 'inherit' className={classes.title2}>
								<Link to='/' className="brand-logo" style={{color : 'inherit'}}>Login</Link>
							</Button>


						</Hidden>
						<Hidden smUp>

							<IconButton edge="start" onClick={handleDrawerToggle} >
								<MenuIcon />
							</IconButton>

							<Typography className={classes.Mobileicon}>
								<Link to='/' className="brand-logo"><img src="./laptop_ng.png" /></Link>
							</Typography>


						</Hidden>


						<Button aria-describedby={id} color='inherit' onClick={handleClick}>
							<ShoppingCartIcon style={{fontSize : 'xl'}}/>
						</Button>


					</Toolbar>
				</AppBar>
				<Drawers 
				mobilebar={mobilebar}
				handleDrawerToggle = {handleDrawerToggle}
				/>


				{/*Window cartpopup*/}

				<Popover 

				  id={id}
                  open={open}
                  anchorEl={anchorEl}
                  onClose={handleClose}

				  anchorOrigin={{
				    vertical: 'bottom',
				    horizontal: 'center',
				  }}
				  transformOrigin={{
				    vertical: 'top',
				    horizontal: 'center',
				  }}


				  anchorPosition = {{ top: 400}}

				>
					<CartPop />
				</Popover>




		    </div>


			);


}

export default NavBar
